(3605, 302, '_apply_section_one_row_two_apply_section_one_row_two_image', 'field_5c44df2d65f67'),
(3606, 302, 'apply_section_one_row_two_apply_section_one_row_two_text', 'If you or someone you know is interested in applying to be a homeowner through Habitat for Humanity, please consider our three criteria:\r\n\r\n1. Need for Housing\r\n2. Ability to Pay\r\n3. Willingness to Partner'),
