INSERT INTO `wp_postmeta` ( `meta_id`, `post_id`, `meta_key`, `meta_value`) VALUES
(10198, 362, 'apply_sfour_expands_6_apply_sfour_exapnds_body', 'House size depends on the number of family members who will be living there. The following is the maximum square footage for each house size: 900 square feet for a two-bedroom house, 1070 square feet for a three bedroom house, 1230 square feet for a four-bedroom house, and 1400 square feet for a five-bedroom house. The square foot numbers describe living space not including stairwells, crawl space and exterior storage.'),
(10199, 362, '_apply_sfour_expands_6_apply_sfour_exapnds_body', 'field_5c44e3227c66a'),
(10268, 364, 'image_hero_banner', '332'),
(10269, 364, '_image_hero_banner', 'field_5c43ccbc4730b'),
(10270, 364, 'apply_section_one_row_one_apply_section_one_row_one_text', 'Habitat for Humanity believes that all hard-working individuals and families should have access to the benefits and stability of homeownership. In this belief, we are committed to partnering with families to build simple, decent, and affordable housing.\r\n\r\nThe low cost of our homes is made possible by the exceptional support of our community. Building costs remain low thanks to impressive donations and generous volunteerism. These factors make it possible to sell our homes to accepted families with a 0% interest loan and at no profit.'),
(10271, 364, '_apply_section_one_row_one_apply_section_one_row_one_text', 'field_5c44de49b4a32'),
